informalexample.screen:
#! /bin/bash
# # Skeleton bash script suitable for starting and stopping # wrapped Java apps on the Linux platform. # # This script expects to find the 'realpath' executable # in the same directory. #
#----------------------------------------------------------------------------- # These settings can be modified to fit the needs of your application
# Application APP_NAME="@app.name@" APP_LONG_NAME="@app.long.name@"
# Wrapper WRAPPER_CMD="./wrapper" WRAPPER_CONF="../conf/wrapper.conf"
# Do not modify anything beyond this point #-----------------------------------------------------------------------------
# Get to the actual location of this script SCRIPT_DIR=`dirname $0` SCRIPT=`$SCRIPT_DIR/realpath $0` cd `dirname $SCRIPT`
# Find pidof. PIDOF="/bin/pidof" if [ ! -x $PIDOF ] then PIDOF="/sbin/pidof" if [ ! -x $PIDOF ] then echo "Cannot find 'pidof' in /bin or /sbin." echo "This script requires 'pidof' to run." exit 1 fi fi
start() { echo "Starting $APP_LONG_NAME..." pid=`$PIDOF $APP_NAME` if [ -z $pid ] then exec -a $APP_NAME $WRAPPER_CMD $WRAPPER_CONF & else echo "$APP_LONG_NAME is already running." exit 1 fi }
stop() { echo "Stopping $APP_LONG_NAME..." pid=`$PIDOF $APP_NAME` if [ -z $pid ] then echo "$APP_LONG_NAME was not running." else kill $pid sleep 6
pid=`$PIDOF $APP_NAME` if [ ! -z $pid ] then kill -9 $pid fi
pid=`$PIDOF $APP_NAME` if [ ! -z $pid ] then echo "Failed to stop $APP_LONG_NAME." else echo "Stopped $APP_LONG_NAME." fi fi }
case "$1" in
'start') start ;;
'stop') stop ;;
'restart') stop start ;;
*) echo "Usage: $0 { start | stop | restart }" exit 1 ;; esac
exit 0

#! /bin/sh
# # Skeleton sh script suitable for starting and stopping # wrapped Java apps on the Solaris platform. # # This script expects to find the 'realpath' executable # in the same directory. # # Make sure that PIDFILE points to the correct location, # if you have changed the default location set in the # wrapper configuration file. #
#----------------------------------------------------------------------------- # These settings can be modified to fit the needs of your application
# Application WRAPPER_NAME="@app.name@" APP_LONG_NAME="@app.long.name@"
# Wrapper WRAPPER_CMD="./wrapper" WRAPPER_CONF="../conf/wrapper.conf"
# Do not modify anything beyond this point #-----------------------------------------------------------------------------
# Get to the actual location of this script SCRIPT_DIR=`dirname $0` SCRIPT=`$SCRIPT_DIR/realpath $0` cd `dirname $SCRIPT`
# Process ID PIDDIR="/var/run" PIDFILE="$PIDDIR/$APP_NAME.pid" pid=""
getpid() { if [ -f $PIDFILE ] then if [ -r $PIDFILE ] then pid=`cat $PIDFILE` if [ "X$pid" != "X" ] then # Verify that a process with this pid is still running. pid=`/usr/bin/ps -p $pid | grep $pid | grep -v grep | awk '{print $1}' | tail -1` if [ "X$pid" = "X" ] then # This is a stale pid file. rm -f $PIDFILE echo "Removed stale pid file: $PIDFILE" fi fi else echo "Cannot read $PIDFILE." rm -f $PIDFILE exit 1 fi fi }
start() { echo "Starting $APP_LONG_NAME..." getpid if [ "X$pid" = "X" ] then exec $WRAPPER_CMD $WRAPPER_CONF & else echo "$APP_LONG_NAME is already running." exit 1 fi }
stop() { echo "Stopping $APP_LONG_NAME..." getpid if [ "X$pid" = "X" ] then echo "$APP_LONG_NAME was not running."
else kill $pid sleep 6
pid=`/usr/bin/ps -p $pid | grep $pid | grep -v grep | awk '{print $1}' | tail -1`
if [ "X$pid" != "X" ] then # SIGTERM didn't work, send SIGKILL. kill -9 $pid rm -f $PIDFILE
pid=`/usr/bin/ps -p $pid | grep $pid | grep -v grep | awk '{print $1}' | tail -1` fi
if [ "X$pid" != "X" ] then echo "Failed to stop $APP_LONG_NAME." exit 1 else echo "Stopped $APP_LONG_NAME." fi fi }
case "$1" in
'start') start ;;
'stop') stop ;;
'restart') stop start ;;
*) echo "Usage: $0 { start | stop | restart }" exit 1 ;; esac
exit 0
